import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { getTemperaments, getDogs } from '../../redux/actions/actions';
import {Link, useHistory} from 'react-router-dom';

export default function Creator() {
    const dispatch = useDispatch();
    
    const [input, setInput] = useState({
         
          breed:"",
          age: "",
          image:"",
          heightMin:"", 
          heightMax:"",  
          weightMax:"",
          weightMin:"",
          temperaments: []

    });

    function ImageUploader({ breed }) {
      const [previewImage, setPreviewImage] = useState(null);
    
      function handleImageUpload(event) {
        const image = event.target.files[0];
        const reader = new FileReader();
        reader.onload = function() {
          setPreviewImage(reader.result);
        };
        reader.readAsDataURL(image);
      }
    

    useEffect(() => {
      dispatch(getTemperaments());
    }, []);

    return (
         <div>
           <h1>Create your own breed!</h1> 
           <form>
            <div>
               <label>Breed:</label> 
               <input 
               type="text"
               input= {input.breed}
               name="breed" 
               />
            </div>
              <div>
                <label>Age:</label>
               <input
               type="number"
               value={input.age}
               name="age"
               />
              </div>
             <div>
                <label>Max-Height</label>
                <input
                type="number"
                value={input.heightMax}
                name="heightMax"
                />
              </div>
              <div>
                <label>Min-Height</label>
                <input
                type="number"
                value={input.heightMin}
                name="heightMin"
                />
              </div>
              <div>
                <label>Min-Weight</label>
                <input
                 type="number"
                 value={input.weightMin}
                 name="weightMin"
                />
              </div>
              <div>
                <label>Max-Weight</label>
                <input
                  type="number"
                  value={input.weightMax}
                  name="weightMax"
                />
              </div>              
              <div>
               <select>
               </select>
              </div>
              <div>
              <label htmlFor="image">Imagen:</label>
              <input type="file" onChange={cargarImagen} name="imagen" accept="image/*" />
              <img src={image} alt={`${breed}`} />
            </div>
           </form>
       
       {/*  <button onClick={() => createBreed({ breed, age, heightMin, heightMax, weightMin, weightMax})}>Create Breed</button>   
        <br></br> */}
        <Link to = "/home"><button>Back Home</button></Link>     
         </div>
    );
  }  
}
