import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { getTemperaments, postBreed } from '../../redux/actions/actions';
import {Link, useHistory} from 'react-router-dom'; 

export default function Creator() {
    const dispatch = useDispatch();
    const history= useHistory();


    const [input, setInput] = useState({   
          breed:"",
          age: "",
          image:"",
          heightMin:"", 
          heightMax:"",  
          weightMax:"",
          weightMin:"",
          temperaments: [],
       });
 

    useEffect(() => {
      dispatch(getTemperaments());
    }, []);
    
    const temperaments = useSelector(state => state.temperaments);
    console.log(temperaments);
    
    const [selectedTemperaments, setSelectedTemperaments] = useState([]);
    //Estado que almacena temps seleccionados.
     
    function handleChange(e){
      setInput({
        ...input, 
        [e.target.name]: e.target.value
      });
      console.log(input);    
    }


    function handleSelect(e) {
      const selectedTemperament = e.target.value;
      setInput({
        ...input, 
        temperaments: [...input.temperaments, selectedTemperament]
      });
      setSelectedTemperaments([...selectedTemperaments, selectedTemperament]);
    }


          function handleSubmit(e){
          e.preventDefault();
          console.log(input)
          dispatch(postBreed(input)).then((response) => {
            console.log(response);
          });
          alert('Breed created successfully')
          setInput({
            ...input,
            breed:"",
            age: "",
            image:"",
            heightMin:"", 
            heightMax:"",  
            weightMax:"",
            weightMin:"",
            temperaments: []
          }); 
        history.push('/home')
        }
    return (
         <div>
           <h1>Create your own breed!</h1> 
           <form onSubmit={(e) => handleSubmit(e)}>
            <div>
               <label>Breed:</label> 
               <input 
               type="text"
               value= {input.breed}
               name="breed" 
               onChange={(e) => handleChange(e)}
               />
            </div>
              <div>
                <label>Age:</label>
               <input
               type="number"
               value={input.age}
               name="age"
               onChange={(e) => handleChange(e)}
               />
              </div>
              <div>
                <label>Image:</label>
                <input
                 type="text"
                 value={input.image}
                 name="image"
                 onChange={(e) => handleChange(e)}
                />
              </div>
              <div>
                <label>Max-Height</label>
                <input
                type="number"
                value={input.heightMax}
                name="heightMax"
                onChange={(e) => handleChange(e)}
                />
              </div>
              <div>
                <label>Min-Height</label>
                <input
                type="number"
                value={input.heightMin}
                name="heightMin"
                onChange={(e) => handleChange(e)}
                />
              </div>
              <div>
                <label>Min-Weight</label>
                <input
                 type="number"
                 value={input.weightMin}
                 name="weightMin"
                 onChange={(e) => handleChange(e)}
                />
              </div>
              <div>
                <label>Max-Weight</label>
                <input
                  type="number"
                  value={input.weightMax}
                  name="weightMax"
                  onChange={(e) => handleChange(e)}
                />
              </div>              
              <div>
              {temperaments.length > 0 && (
                <select onChange={(e) => handleSelect(e)}>
                  <option value="temperaments">All Temperaments:</option>
                  <select name="temperaments" value={input.temperaments} onChange={handleChange} multiple></select>
                  {temperaments.map((temp) => (
                    <option key={temp} value={temp}>{temp}</option>
                  ))}
                </select>
              )}
              <p> Selected Temperaments: {selectedTemperaments.join(', ')}</p>
              </div>
              <button type= "submit">Create Breed</button>  
              <br></br>
              </form>
              <Link to = "/home"><button>Back Home</button></Link>        
         </div>
    );
  }
