import React from "react";
import styles from '../PAGINADO/Paginado.module.css'


function Paginado ({dogsPerPage, dogs, paginado, currentPage}){ // declaro mi paginado, traigo las props del otro componente
  const pageNumber = []

  for (let i = 0; i <= Math.ceil(dogs/dogsPerPage); i++) {
    pageNumber.push(i+1);
  }


  const previousPage = () => {
    if (currentPage > 1) {
      paginado(currentPage - 1);
    }
  };

  const nextPage = () => {
    if (currentPage < pageNumber.length) {
      paginado(currentPage + 1);
    }
  };

  return (
    <nav className={styles.nav}>
        <div className={styles.paginado}>
        <div className={styles.number}>
            <button onClick={previousPage} disabled={currentPage === 1}>
              Prev
            </button>
          </div>
           {pageNumber?.map(number => (
            <div className={styles.number} key={number}>
            <button type="button" onClick={() => paginado(number)}>{number}</button>   
          </div>
           ))}
              <div className={styles.number}>
            <button onClick={nextPage} disabled={currentPage === pageNumber.length}>
              Next
            </button>
          </div>
        </div>
    </nav>
  )
}
export default Paginado;
