import {createStore, apllyMiddleware, compose}  from "redux"; 
import reducer from ".reducer";
import thunkMiddleware from "redux-thunk";

const composeEnhancer = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose // nos conecta con la extension del navegador.

const store = createStore(
    reducer,
    composeEnhancer(applyMiddleware(thunkMiddleware))  // sirve para hacer peticiones a la API
    )


    export default store;
