import React, {useState,  useEffect } from "react";
import { useDispatch , useSelector} from "react-redux";
import { getDogs } from "../../redux/actions/actions";
import { Link } from "react-router-dom";
import Card from "../CARD/Card";
import Paginado from "../PAGINADO/Paginado";






function Home() {
  const dispatch = useDispatch();
  const dogs = useSelector((state) => state.dogs);

  //PAGINADO:
  const [currentPage, setCurrentPage]=useState(1) //mi pagina actual que arraca en 1
  const [dogsPerPage, setDogsPerPage]=useState(8) // Mis perros por pagina que son 8
  const indexOfLastDog= currentPage * dogsPerPage;
  const indexOfFirstDog= indexOfLastDog - dogsPerPage;
  const currentDogs= dogs.slice(indexOfFirstDog, indexOfLastDog);  //const que guarda todos los perros que voy a tener por pagina.

  const paginado = (pageNumber) =>{  
   setCurrentPage(pageNumber)
  }


  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  function handleClick(event) {
    event.preventDefault();
    dispatch(getDogs());
  }

  const handleFiltroTemp = (e) => {
    createdBox.current.selectedIndex = 0;
    setPagActual(1);
    setMinNP(0);
    setMaxNP(5);
    dispatch(filterByTemperament(e.target.value));
  }

  return (
    <div>
      <Link to="/dogs">Create Breed</Link>
      <h1>We Love Dogs</h1>
      <button onClick={(event) => handleClick(event)}>
        All breeds!
      </button>
      <div>
        <select>
          <option value="asc">Ascending</option>
          <option value="desc">Descending</option>
        </select>
        <select>
          
        </select>
        <select>
          <option value="all">All Dogs</option>
          <option value="created">Created</option>
          <option value="api">Existent</option>
        </select>
        <Paginado 
        dogsPerPage={dogsPerPage}
        dogs={dogs.length}
        paginado={paginado}
        />
        {currentDogs?.map((dog) => {
          return (
                <Card
                  key= {dog.id}
                  id = {dog.id}
                  name = {dog.name}
                  image = {dog.image}
                  weight = {dog.weight}
                  height = {dog.height}
                  age = {dog.age}
                  temperaments = {dog.temperaments}
                  temperament = {dog.temperament}
                  createInDb = {dog.createInDb}
                />
          );
        })}
      </div>
    </div>
  );
}

export default Home;
