import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { getTemperaments, postBreed } from '../../redux/actions/actions';
import {Link, useHistory} from 'react-router-dom'; 


function validation (input){
  let errors = {};
  if (!input.name){
    errors.name = "Breed is required"
 } else if (!input.height) {
  errors.height = "Height is required"
  if(input.height < 20 || input.height > 25) {
    errors.height = 'The height must be between "20 cm" and "70 cm"'; 
   }
  } else if (!input.weight){
    errors.weight ="Weight is required"
    if(input.weight < 1 || input.weight > 82)
    errors.weight ='The weigth must be between "1 kg" and "82 kg"'
  } else if (!input.age) {
     errors.age = 'Age is required'
     if(input.age > 20) {
      errors.age = 'Age should be under 20'
     }
    
  } else if (!input.image){
    errors.image = 'Image is required'
  } else if (!input.temperament){
    errors.temperament = 'Must select at least one temperament'
  }
  return errors
}





export default function Creator() {
    const dispatch = useDispatch();
    const history= useHistory();


    const [input, setInput] = useState({   
          id:"",
          name:"",
          height:"", 
          weight:"",
          age: "",
          image:"",
          createInDb:"",
          temperament:[], 
          temperaments:[]
       });
 

    useEffect(() => {
      dispatch(getTemperaments());
    }, []);
    
    const temperaments = useSelector(state => state.temperaments);

    console.log(temperaments)
  
    
    const [selectedTemperaments, setSelectedTemperaments] = useState([]);
    //Estado que almacena temps seleccionados.
    const [errors, setErrors] = useState({})
    const [disabledButtons, setDisabledButtons] = useState([]);

    
    function handleChange(e){
      setInput({
        ...input, 
        [e.target.name]: e.target.value
      });
      setErrors(validation({
        ...input,
        [e.target.name]: e.target.value
      }))  
    }



    function handleSelect(e) {
      const selectedTemperament = e.target.value;
      setInput(prevState => ({
        ...prevState,
        temperament: [...prevState.temperament, selectedTemperament],
        temperaments: [...prevState.temperaments, selectedTemperament]
      }));
      setSelectedTemperaments(
        [...selectedTemperaments, selectedTemperament]);
    }
    


    function handleSubmit(e){
      e.preventDefault();
      const errors = validation(input); // add this line
      if (Object.keys(errors).length === 0) { // add this conditional
        dispatch(postBreed(input)).then((response) => {
          console.log(response);
        });
        alert('Breed created successfully')
        setInput({
          id:"",
          name:"",
          height:"", 
          weight:"",
          age: "",
          image:"",
          createInDb:"",
          temperament:[]
        }); 
        setSelectedTemperaments([]);
        history.push('/home')
      } else {
        alert('You are missing datails, please check!')
      }
    }
    
    function handleDelete(temp) {
      setSelectedTemperaments(selectedTemperaments.filter(t => t !== temp));
      setInput(prevState => ({
        ...prevState,
        temperament: prevState.temperament.filter(t => t !== temp)
      }));
    }

    return (
         <div>
           <h1>Create your own breed!</h1> 
           <form onSubmit={(e) => handleSubmit(e)}>
            <div>
               <label>Breed:</label> 
               <input 
               type="text"
               value= {input.name}
               name="name" 
               onChange={(e) => handleChange(e)}
               />
               {errors.name && (<p>{errors.name}</p>)}
            </div>
            <div>
                <label>Height:</label>
                <input
                type="number"
                value={input.height}
                name="height"
                onChange={(e) => handleChange(e)}
                />
                {errors.height && (<p>{errors.height}</p>)}
              </div>
              <div>
                <label>Weight:</label>
                <input
                 type="number"
                 value={input.weight}
                 name="weight"
                 onChange={(e) => handleChange(e)}
                />
                {errors.weight && (<p>{errors.weight}</p>)}
              </div>  
              <div>
                <label>Age:</label>
               <input
               type="number"
               value={input.age}
               name="age"
               onChange={(e) => handleChange(e)}
               />
               {errors.age && (<p>{errors.age}</p>)}
              </div>
              <div>
                <label>Image:</label>
                <input
                 type="text"
                 value={input.image}
                 name="image"
                 onChange={(e) => handleChange(e)}
                />
                {errors.image && (<p>{errors.image}</p>)}
              </div>
              <p>All Temperaments:</p> 
              {temperaments && temperaments.length > 0 && (
                <div>
   
                 <select onChange={(e) => handleSelect(e)} name="temperament" value={selectedTemperaments} multiple>
                    {Object.keys(temperaments).map((key,index) => (
                      <option key={index}>{temperaments[key]}</option>
                    ))}
                  </select>
                  {selectedTemperaments.length > 0 ? (
                    <p>Selected Temperaments: {selectedTemperaments.join(", ")}</p>
                  ) : null}
                    {selectedTemperaments.map((temp, index) => (
                    <button key={index} onClick={() => handleDelete(temp)}>{input.temperament}</button>
                  ))}
                   {errors.temperament && (<p>{errors.temperament}</p>)}
                </div>
              )}
              <button type= "submit">Create Breed</button>  
              <br></br>
              </form>
              <Link to = "/home"><button>Back Home</button></Link>        
         </div>
    );
  }
