import React, {useState,  useEffect } from "react";
import { useDispatch , useSelector} from "react-redux";
import { getDogs , getTemperaments, FilterByTemperament,filterCreated,getByName} from "../../redux/actions/actions";
import { Link } from "react-router-dom";
import Card from "../CARD/Card";
import Paginado from "../PAGINADO/Paginado";
import SearchBar from '../SEARCH BAR/SearchBar';

function Home() {
  const dispatch = useDispatch();
  //valores del estado local de redux que necesito:
  const dogs = useSelector((state) => state.dogs);
  const temperamentsState = useSelector(state => state.temperaments);

  //PAGINADO:
  const [currentPage, setCurrentPage]= useState(1) //mi pagina actual que arraca en 1
  const [dogsPerPage, setDogsPerPage]= useState(8) // Mis perros por pagina que son 8
  const indexOfLastDog= currentPage * dogsPerPage;
  const indexOfFirstDog= indexOfLastDog - dogsPerPage;
  const currentDogs= dogs.slice(indexOfFirstDog, indexOfLastDog);  //const que guarda todos los perros que voy a tener por pagina.

  const paginado = (pageNumber) =>{  
   setCurrentPage(pageNumber)
  }
  

useEffect(() => {
  //acciones a depachar luego de montar el componente
  dispatch(getDogs());
  dispatch(getTemperaments());
}, [dispatch]);

  function handleClick(event) {
    event.preventDefault();
    dispatch(getDogs());
  }
  
  const handleFilterByTemperament = (e) => {
    e.preventDefault();    
    dispatch(FilterByTemperament(e.target.value));
  };

  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (searchValue) => {
    // Aquí podrías realizar la búsqueda de datos y actualizar setSearchResults con los resultados
    console.log(`Buscando "${searchValue}"...`);
  
  return (
    <div>
      <Link to="/dogs">Create Breed</Link>
      <h1>We Love Dogs</h1>
      <SearchBar onSearch={handleSearch}/>
         {/* Aquí van los resultados de la búsqueda */}
         {searchResults.length > 0 && (
        <ul>
          {searchResults.map((result) => (
            <li key={result.id}>{result.name}</li>
          ))}
        </ul>
      )} 
      <button onClick={(event) => handleClick(event)}>
        All breeds!
      </button>
      <div>

        <select>
          <option value="asc">Ascending</option>
          <option value="desc">Descending</option>
        </select>

        <select>
          <option value="all">All dogs</option>
          <option value="created">Created</option>
          <option value="api">Existent</option>
        </select>

        <select  onChange={(event) => handleFilterByTemperament(event)}>
          <option value ='All'>All Temperaments</option>
          {temperamentsState?.map(t => (
                <option key={t} value={t}>
                  {t}
                </option>
                ))}
        </select>
        
        <Paginado 
        dogsPerPage={dogsPerPage}
        dogs={dogs.length}
        paginado={paginado}
      
        />
        {currentDogs?.map((dog) => {
          return (
            <Card
              key={dog.id}
              id={dog.id}
              name={dog.name}
              image={dog.image}
              weight={dog.weight}
              temperaments={dog.temperaments}
              temperament={dog.temperament}
            />
          );
        })}
        
      </div>
    </div>
  );
 }
}

export default Home;
