// estado inicial de nuestro reducer.
// acá traigo la lista de las razas, los temperamentos, y los detalles (si se pidieron)
// dogsCopy es una copia de dogs. sobre esta se hacen los filtros (para evitar problemas con array ya filtrado)


const initialState = {
    dogs: [],
    details: [],
    dogsCopy: [],
    temperaments: [],
};

function rootReducer(state = initialState, action) {
    switch(action.type) {
        case 'GET_DOGS':
            return {
                ...state,
                dogs: action.payload
            }

        case "GET_TEMPERAMENTS":
                const filteredTemp = action.payload.filter((temp) => temp.name !== ""); //eliminar razas con strings vacios
                return {
                  ...state,
                  temperaments: filteredTemp,
                };       
        case "GET_FILTER_TEMPERAMENTS":
                    const allDogs = state.allDogs;
                    let filteredDogs = [];
                    if (action.payload === "All") {
                      filteredDogs = allDogs;
                    } else {
                      for (let i = 0; i < allDogs.length; i++) {
                        let found = allDogs[i].temperaments.find((t) => t === action.payload);
                        if (found) {
                          filteredDogs.push(allDogs[i]);
                        } //todos los perros en la posicion de ese momento
                      }
                    }
                    return {
                      ...state,
                      dogs: filteredDogs,
                    };     
   
                      default:
                      return state;
    }
}
export default rootReducer;
