// estado inicial de nuestro reducer.
// acá traigo la lista de las razas, los temperamentos, y los detalles (si se pidieron)
// dogsCopy es una copia de dogs. sobre esta se hacen los filtros (para evitar problemas con array ya filtrado)


const initialState = {
    dogs: [],
    allDogs: [],
    details: [],
    dogsCopy: [],
    temperaments: [],
};

function rootReducer(state = initialState, action) {
    switch(action.type){
        case 'GET_DOGS':
            return {
                ...state,
                dogs: action.payload
            }
         case 'FILTER_BY_STATUS':
            const allDogs= state.dogsCopy
            const statusFiltered = action.payload === 'created'? all.filter(el => el.createInDb) : allDogs.filter(el => !el.createInDb)
            return {
                ...state,
                dogs: action.payload === "all" ? state.dogsCopy : statusFiltered
            }
        case 'GET_TEMPERAMENTS':
            
                return {
                    ...state,
                    temperaments: action.payload
                }   
            default :
            return state;

        
    }
}
export default rootReducer;
