import axios from 'axios';
import {GET_DOGS,GET_TEMPERAMENTS,GET_FILTER_TEMPERAMENTS,GET_BY_NAME,GET_DOG_BY_ID} from "./action-types";

export function getDogs() {
  return async function(dispatch) {
    let json = await axios.get("http://localhost:3001/dogs");
    return dispatch({
      type: GET_DOGS,
      payload: json.data
    });
  };
}


export function getTemperaments() {
  return async function (dispatch) {
      try {
          const res = await axios.get('http://localhost:3001/temps')
          dispatch({ type: GET_TEMPERAMENTS, payload: [...res.data.map(temp => temp.name)] })
      } catch (error) {
          alert(error.message)
      }
  };
  
}

export function FilterByTemperament(payload) {
  return{
      type: GET_FILTER_TEMPERAMENTS,
      payload
  }
};

export function getByName(name) {
  return async function (dispatch) {
      try {
          const res = await axios.get(`http://localhost:3001/dogs?name=${name}`)
          dispatch({ type: GET_BY_NAME, payload: res.data })
      } catch (err) {
          dispatch({ type: GET_BY_NAME, payload: console.log(err.response.data)})
      }
  };
}

export const getDogById = (id) => {
  return async function (dispatch) {
    try {
      const response = await axios.get(`http://localhost:3001/dogs/${id}`);
      const dog = response.data  
      dispatch({ type: GET_DOG_BY_ID, payload: dog });
        } catch (error) {
      console.log(error);
    }
  };
}
