// estado inicial de nuestro reducer.
// acá traigo la lista de las razas, los temperamentos, y los detalles (si se pidieron)
// dogsCopy es una copia de dogs. sobre esta se hacen los filtros (para evitar problemas con array ya filtrado)


const initialState = {
    dogs: [],
    details: [],
    dogsCopy: [],
    temperaments: [],
};

function rootReducer(state = initialState, action) {
    switch(action.type){
        case 'GET_DOGS':
            return {
                ...state,
                dogs: action.payload
            }
         case 'FILTER_BY_STATUS':
            const dogsCopy= state.dogsCopy
            const statusFiltered = action.payload === 'created'? dogsCopy.filter(el => el.createInDb) : allDogs.filter(el => !el.createInDb)
            return {
                ...state,
                dogs: action.payload === "all" ? state.dogsCopy : statusFiltered
            }
        case 'GET_TEMPERAMENTS':
                return {
                    ...state,
                    temperaments: action.payload
                }   
        case 'GET_BY_NAME':
                    return {
                        ...state,
                        dogs: action.payload
                    }
        
        
        case 'GET_BY_ID':
                    return {
                        ...state,
                        details: action.payload
                    }

        
    
                    case 'SORT_BY_TEMPERAMENT':
                        const allDogs = state.dogsCopy;
                        const filterDog = (action.payload === 'All') ? allDogs : allDogs.filter(e => {
                          if (typeof e.temperament === 'string') {
                            return e.temperament === action.payload;
                          } else if (Array.isArray(e.temperament)) {
                            return e.temperament.includes(action.payload);
                          }
                          return false;
                        });
                        return {
                          ...state,
                          dogs: filterDog
                        };

                        case FILTERED_BY_GENRES:
            const allVideogames = state.allVideogames;
            const filteredGenre = action.payload === 'All'? allVideogames : allVideogames.filter((videogame) => videogame.genres?.find(v => v === action.payload));
            return {
                ...state,
                videogames: filteredGenre
            }
            default :
            return state;

        
    }
}
export default rootReducer;
