import React, { useState } from 'react';

function SearchBar({ onSearch }) {
  const [searchValue, setSearchValue] = useState('');

  const handleChange = (event) => {
    const value = event.target.value;
    setSearchValue(value);
    onSearch(value); // llamada a la función onSearch pasando el valor actualizado de búsqueda
  };

  return (
    <div>
      <label htmlFor="search-input">Breed Finder</label>
      <input
        id="search-input"
        type="text"
        placeholder="Search"
        value={searchValue}
        onChange={handleChange} // actualiza el estado de búsqueda en cada cambio del input
      />
    </div>
  );
}

export default SearchBar;
